import React, { Component } from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import FeedbackList from './components/LeaveFeedback/FeedbackList';
import Statistics from './components/LeaveFeedback/Statistics';

export default function Feedbacks() {
  const [goodFeedback, setGoodFeedback] = useState(0);
  const [neutralFeedback, setNeutralFeedback] = useState(0);
  const [badFeedback, setBadFeedback] = useState(0);
  const [summ, setSumm] = useState(0);

  const handleBtnClick = (e) => {
    const name = e.target.name;

    switch (name) {
      case 'good':
        setGoodFeedback((goodFeedback) => (goodFeedback += 1));
        break;
      case 'neutral':
        setNeutralFeedback((neutralFeedback) => (neutralFeedback += 1));
        break;
      case 'bad':
        setBadFeedback((badFeedback) => (badFeedback += 1));
        break;

      default:
        return;
    }
  };

  useEffect(() => {
    function countSumm() {
      return goodFeedback + neutralFeedback + badFeedback;
    }

    setSumm((summ) => countSumm());
    document.title = summ;
  }, [goodFeedback, neutralFeedback, badFeedback, summ]);

  return (
    <div>
      <ul>
        <li>
          <button onClick={handleBtnClick} name="good">
            SetGoodFeedback
          </button>
          <p>{goodFeedback}</p>
        </li>
        <li>
          <button onClick={handleBtnClick} name="neutral">
            SetGoodFeedback
          </button>
          <p>{neutralFeedback}</p>
        </li>
        <li>
          <button onClick={handleBtnClick} name="bad">
            SetGoodFeedback
          </button>
          <p>{badFeedback}</p>
          <p>{summ}</p>
        </li>
      </ul>
    </div>
  );
}

class OldFeedbacks extends Component {
  state = {
    good: 0,
    neutral: 0,
    bad: 0,
  };

  increaseValue = (name) => {
    this.setState((prevState) => {
      return {
        [name]: prevState[name] + 1,
      };
    });
  };

  countTotalFeedbacks = () => {
    return Object.values(this.state).reduce((acc, val) => acc + val, 0);
  };

  countPositiveFeedbacks = () => {
    const total = this.countTotalFeedbacks();
    const good = this.state.good;

    return Math.floor((good * 100) / total) + '%';
  };

  render() {
    const feedbacks = this.state;
    const state = this.state;

    return (
      <section>
        <h1>Homework</h1>
        <FeedbackList feedbacks={feedbacks} onIncreaseValue={this.increaseValue} />
        <Statistics
          good={state.good}
          neutral={state.neutral}
          bad={state.bad}
          totalFeedbacks={this.countTotalFeedbacks()}
          positiveFeedbacks={this.countPositiveFeedbacks()}
        />
      </section>
    );
  }
}
